import React from "react";



// GET파라미터 추출 기능을 갖는 useLocation() 함수를 react-router-dom 패키지로부터 참조함.

import { useLocation } from "react-router-dom";

const DepartmentGet = () => {
    console.clear();
    const location = useLocation();
    console.group("uselocation()리턴값 확인");
    console.debug(location);
    console.groupEnd();

    const {search} = location;
    const query = new URLSearchParams(search);
    console.group("QueryString 확인");
    console.debug(query);
    console.groupEnd();

    console.group("파라미터 처리 결과 확인");
    console.debug("요청된 학과번호 값 %s", query.get('id', typeof query.get('id')));
    console.debug("요청된 메시지 내용 %s", query.get('msg', typeof query.get('msg')));
    console.groupEnd();

    //  생성된 객체를 JSON으로 변환
    console.group("파라미터 JSON 변환 확인");
    const params = Object.fromEntries(query);
    console.debug(params);
    const {id, msg} = params;
    console.debug('요청된 학과번호 값 = %s (%s)', id, typeof id);
    console.debug('요청된 메시지 = %s (%s)', msg, typeof msg);
    console.groupEnd();
    return(
        <div>
            <h1>DepartmentGet haha</h1>

        </div>
    )
}

export default DepartmentGet;
